import React, {useState} from 'react';
import Header from './Header';
import Footer from './Footer';
import Channels from './Channels';
import Messages from './Messages';
import './App.css';

const styles = {
    root: {
        boxSizing: 'border-box',
        display: 'flex',
        flexDirection: 'column',
        backgroundColor: '#565E71',
        padding: 50
    },
    main: {
        backgroundColor: '#373B44',
        flex: '1 1 auto',
        display: 'flex',
        flexDirection: 'row',
        overflow: 'hidden'
    },
    channel: {
        height: '100%',
        flex: '1 1 auto',
        display: 'flex',
        flexDirection: 'column',
        overflow: 'hidden'
    },
    form: {
        borderTop: '2px solid #373B44',
        padding: '.5rem',
        display: 'flex'
    },
    content: {
        flex: '1 1 auto',
        marginRight: '.5rem'
    },
    send: {
        backgroundColor: '#D6DDEC',
        padding: '.2rem .5rem',
        border: 'none',
        ':hover': {
            backgroundColor: '#2A4B99',
            cursor: 'pointer',
            color: '#fff'
        }
    }
};

const MessageForm = ({
                         addMessage
                     }) => {
    const [content, setContent] = useState('');

    //You can improve this function with one hook (useCallback) :
    // https://fr.reactjs.org/docs/hooks-intro.html
    // https://fr.reactjs.org/docs/hooks-reference.html
    const onSubmit = (e) => {
        addMessage({
            content,
            author: 'david',
            creation: Date.now(),
        });

        setContent('');
    };

    //You can improve this function with one hook (useCallback) :
    const onChange = (e) => {
        setContent(e.target.value);
    };

    return (
        <div style={styles.form}>
            <textarea
                onChange={onChange}
                name="content"
                rows={5}
                style={styles.content}
                value={content}
            />
            <button onClick={onSubmit} type="submit" style={styles.send}>
                Send
            </button>
        </div>
    )
};

export default ({
                }) => {

    return (
        <div className="app" style={styles.root}>
            <Header/>
            <main className='App-main' style={styles.main}>
                <Channels/>
                <div style={styles.channel}>
                    <Messages/>
                    <MessageForm addMessage={addMessage} />
                </div>
            </main>
            <Footer/>
        </div>
    );
}
